import React, {useState} from "react";
import anime from "animejs";


const navbarAnimation = () => {

    const navRef = React.useRef(null)
    React.useEffect(() => {

            navRef.current =
                anime.timeline({ loop: false })
                    .add({
                        targets: document.getElementById("nav-item"),
                        translateY: [-15, 0],
                        opacity: [0, 1],
                        duration: 1500,
                        delay: (el, i) => i * 200 + 2100,
                    })
                    .add({
                        targets: document.getElementById("nav-logo"),
                        translateY: [-15, 0],
                        opacity: [0, 1],
                        easing: 'easeOutExpo',
                        duration: 1500,
                    })

    }, [])
}

export default navbarAnimation
