import React, {useState} from "react";


const carousel = () => {
    let images = [
        '../../public/images/ecotrails/BlueLakes/BlueLakes1.jpg',
        '../../public/images/ecotrails/BlueLakes/BlueLakes2.jpg',
        '../../public/images/ecotrails/BlueLakes/BlueLakes3.jpg',
        '../../public/images/ecotrails/BlueLakes/BlueLakes4.jpg'
    ]
    let [imageIndex, setImageIndex] = useState()

    React.useEffect( () => {
        setTimeout(setImageIndex((imageIndex + 1)/3), 2500)
    }, [])
    return images[imageIndex]
}


export default carousel
